void animation(){
    bool animationExecuted = false;

    if (!animationExecuted) { 
    // Frame1
    display.clearDisplay();
    display.drawBitmap(0,0,Frame1, 128, 64, 1);
    display.display();
    delay(frame_delay);
  
    // Frame2
    display.clearDisplay();
    display.drawBitmap(0,0,Frame2, 128, 64, 1);
    display.display();
    delay(frame_delay);
  
    // Frame3
    display.clearDisplay();
    display.drawBitmap(0,0,Frame3, 128, 64, 1);
    display.display();
    delay(frame_delay);
  
    // Frame4
    display.clearDisplay();
    display.drawBitmap(0,0,Frame4, 128, 64, 1);
    display.display();
    delay(frame_delay);

    // Frame5
    display.clearDisplay();
    display.drawBitmap(0,0,Frame5, 128, 64, 1);
    display.display();
    delay(frame_delay);
  
    // Frame6
    display.clearDisplay();
    display.drawBitmap(0,0,Frame6, 128, 64, 1);
    display.display();
    delay(frame_delay);
  
    // Frame7
    display.clearDisplay();
    display.drawBitmap(0,0,Frame7, 128, 64, 1);
    display.display();
    delay(frame_delay);

    if (frame_delay > 50) frame_delay = frame_delay - 20;

    animationExecuted = true;  // Marca a animação como executada
  }
}

void setup(){

  Serial.begin(115200);

  pinMode(ledPinazul, OUTPUT);
  digitalWrite(ledPinazul, LOW);

  pinMode(LED_BUILTIN, OUTPUT);  
  digitalWrite(LED_BUILTIN, LOW);

  pinMode(ledPinamarelo, OUTPUT);
  digitalWrite(ledPinamarelo, LOW);

  pinMode(LED_BUILTIN, OUTPUT);  
  digitalWrite(LED_BUILTIN, LOW);

  if (!display.begin(SSD1306_SWITCHCAPVCC, OLED_I2C_ADDR)) {
    Serial.println(F("Erro ao inicializar o display OLED"));
    while (true);
  }
  display.display();  
  delay(2000);

  display.clearDisplay();
  display.setTextSize(1);      
  display.setTextColor(SSD1306_WHITE);  
  display.setCursor(0, 0);
  animation();
  delay(1000);
  display.clearDisplay();     
  display.print("Servidor Web ESP32");

  Serial.print("Conectando ao Wi-Fi...");
  WiFi.begin(ssid, password);

  int timeout = 0;
  while (WiFi.status() != WL_CONNECTED && timeout < 30) { 
    delay(500);
    Serial.print(".");
    timeout++;
  }

  if (WiFi.status() == WL_CONNECTED) {
    Serial.println("\nConectado ao Wi-Fi!");
    Serial.println("Endereço IP: " + WiFi.localIP().toString()); 
    delay(500); 
    display.clearDisplay();
    animation();
    delay(500);
    display.clearDisplay(); 
    display.setCursor(0, 10);  
    display.print("IP: ");
    display.print(WiFi.localIP()); 
    display.display(); 
  } else {
    Serial.println("\nFalha ao conectar ao Wi-Fi");
    display.clearDisplay();  
    display.setCursor(0, 10);
    display.print("Falha Wi-Fi");
    display.display(); 
  }
  server.begin();
}

void loop() {
  WiFiClient client = server.available(); 
  if (!client) {
    return; 
  }

  String request = client.readStringUntil('\r');
  Serial.println("Requisição recebida: " + request);
  client.flush();

  if (request.indexOf("/LEDazul=ON") != -1) {
    digitalWrite(ledPinazul, HIGH); 
  } else if (request.indexOf("/LEDazul=OFF") != -1) {
    digitalWrite(ledPinazul, LOW); 
  }

  if(request.indexOf("/LEDamarelo=ON") != -1){
    digitalWrite(ledPinamarelo, HIGH);
  } else if(request.indexOf("/LEDamarelo=OFF") != -1){
    digitalWrite(ledPinamarelo, LOW);
  }

  String html = "HTTP/1.1 200 OK\r\nContent-Type: text/html\r\nConnection: close\r\n\r\n";
  html += "<!DOCTYPE HTML><html><head>";
  html += "<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">";
  html += "<style>";
  html += "body { font-family: 'Arial', sans-serif; text-align: center; margin: 0; padding: 0; background-color: #121212; color: #ffffff; }";
  html += "header { background-color: #1f1f1f; padding: 20px; font-size: 24px; color: #ffffff; box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.5); }";
  html += "main { padding: 20px; }";
  html += "button { font-size: 18px; padding: 15px 25px; margin: 15px; border: none; border-radius: 8px; cursor: pointer; width: 80%; max-width: 300px; transition: all 0.3s ease-in-out; }";
  html += ".on { background-color: #4caf50; color: #ffffff; }";
  html += ".on:hover { background-color: #45a049; transform: scale(1.05); }";
  html += ".off { background-color: #f44336; color: #ffffff; }";
  html += ".off:hover { background-color: #d32f2f; transform: scale(1.05); }";
  html += ".footer { margin-top: 20px; font-size: 14px; color: #aaaaaa; }";
  html += "@media (max-width: 600px) { button { width: 90%; font-size: 16px; } }"; 
  html += "</style>";
  html += "</head><body>";
  html += "<header>Controle dos leds</header>";
  html += "<main>";
  html += "<button class=\"on\" onclick=\"location.href='/LEDazul=ON'\">Ligar LED Azul</button>";
  html += "<button class=\"off\" onclick=\"location.href='/LEDazul=OFF'\">Desligar LED Azul</button>";
  html += "<img src='https://media0.giphy.com/media/v1.Y2lkPTc5MGI3NjExOTVxd2JudnpiMmx6YngwbW1vcmVkb3Vkbzg4Nm92azcycXBkNnRxYSZlcD12MV9pbnRlcm5hbF9naWZfYnlfaWQmY3Q9Zw/bqSkJ4IwNcoZG/giphy.webp' alt='anime' width='128' height='64'>";
  html += "<br>";
  html += "</br>";
  html += "<button class=\"on\" onclick=\"location.href='/LEDamarelo=ON'\">Ligar LED Amarelo</button>";
  html += "<button class=\"off\" onclick=\"location.href='/LEDamarelo=OFF'\">Desligar LED Amarelo</button>";
  html += "</main>";
  html += "</body></html>";

  client.print(html);

  delay(1);
  client.stop();
  Serial.println("Cliente desconectado.");
}